package hardik.code;

public class ListNode {

	int data;
	ListNode next;
	
	
	public ListNode() {
		super();
		// TODO Auto-generated constructor stub
	}
	public ListNode(int data, ListNode next) {
		super();
		this.data = data;
		this.next = next;
	}
	public int getData() {
		return data;
	}
	public void setData(int data) {
		this.data = data;
	}
	public ListNode getNext() {
		return next;
	}
	public void setNext(ListNode next) {
		this.next = next;
	}
	
	
}
